function alarm(){
echo " " > /tmp/alarm.txt
for var in 1 
do
echo -e "beep $beepsong " >> /tmp/alarm.txt
done
echo "Adding alarm for" $1 "minutes"
at -M  -f /tmp/alarm.txt now + $1 minutes
}
alias al='alarm '
alias a60='alarm 60'
alias a50='alarm 50'
alias a40='alarm 40'
alias a30='alarm 30'
alias a20='alarm 20'
alias a10='alarm 10'

alias a55='alarm 55'
alias a45='alarm 45'
alias a35='alarm 35'
alias a25='alarm 25'
alias a15='alarm 15'
alias a5='alarm 5'
alias atclear='atrm $(atq|egrep -o "^[0-9]+")'
