#'for var in N' controls number of song repeats(1=default)
function alarm(){
echo " " > /tmp/alarm.txt
for var in 1 
do
echo -e "beep $ffsong " >> /tmp/alarm.txt
done
at -M -f /tmp/alarm.txt now + $1 minutes
}
alias al='alarm '
alias a60='alarm 60'
alias a50='alarm 50'
alias a40='alarm 40'
alias a30='alarm 30'
alias a20='alarm 20'
alias a10='alarm 10'

